import axios from 'axios';

axios.defaults.baseURL = process.env.REACT_APP_URL;

export default {
  getTasks: async (setItems) => {
    try {
      console.log('Requesting:', `${process.env.REACT_APP_URL}/selectAll`);
      const result = await axios.get("https://todoapi-2l8v.onrender.com/selectAll");
      console.log("Data received from API:", result.data);
      if (Array.isArray(result.data)) {
        setItems(result.data); // עדכון ה-state אם מדובר במערך
      } else {
        console.log("Data is not an array");
      }
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  },

  addTask: async (name) => {
    console.log('addTask', name);
    try {
      const result = await axios.post(`add?Name=${encodeURIComponent(name)}`);
      return result.data;
    } catch (err) {
      console.error('Error adding task:', err);
    }
  },

  setCompleted: async (id, isComplete) => {
    console.log('setCompleted', { id, isComplete });
    try {
      const result = await axios.patch(`update/${id}`, isComplete, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      return result.data;
    } catch (err) {
      console.error('Error setting completion:', err);
    }
  },

  deleteTask: async (id) => {
    console.log('deleteTask', id);
    try {
      const result = await axios.delete(`/delete/${id}`);
      return result.data;
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  }
};
