import axios from 'axios';

const apiUrl = "http://localhost:5144"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/selectAll`)    
    return result.data;
  },

  // addTask: async(name)=>{
  //   console.log('addTask', name)
  //   const result = await axios.post(`${apiUrl}/add`,{name} )    
  //   return result.data;
  // },
  addTask: async (name) => {
    console.log('addTask', name);
    const result = await axios.post(`${apiUrl}/add?Name=${encodeURIComponent(name)}`);
    return result.data;
},


  // setCompleted: async(id, isComplete)=>{
  //   console.log('setCompleted', {id, isComplete})
  //   const result = await axios.patch(`${apiUrl}/update/${id}`,{isComplete})    
  //   return result.data;
  // },
 setCompleted: async (id, isComplete) => {
    console.log('setCompleted', { id, isComplete });
    const result = await axios.patch(`${apiUrl}/update/${id}`, { IsComplete: isComplete }, {
        headers: {
            'Content-Type': 'application/json'
        }
    });    
    return result.data;
},

  deleteTask:async(id)=>{
    console.log('deleteTask')
    const result = await axios.delete(`${apiUrl}/delete/${id}`)    
    return result.data;
  }

};
