import axios from 'axios';

const apiUrl = "http://localhost:5144"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/selectAll`)    
    return result.data;
  },

  // addTask: async(name)=>{
  //   console.log('addTask', name)
  //   const result = await axios.post(`${apiUrl}/add`,{name} )    
  //   return result.data;
  // },
  addTask: async (name) => {
    console.log('addTask', name);
    const result = await axios.post(`${apiUrl}/add?Name=${encodeURIComponent(name)}`);
    return result.data;
},


