import axios from 'axios';

const apiUrl = "http://localhost:5144"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/selectAll`)    
    return result.data;
  },

  addTask: async (Name) => {
    const result = await axios.post(`${apiUrl}/add?Name=${encodeURIComponent(Name)}`);
    return result.data;
},

setCompleted: async (id, isComplete) => {
  console.log('setCompleted', { id, isComplete });
  try {
    const result = await axios.patch(`update/${id}?IsComplete=${isComplete}`);
    return result.data;
  } catch (err) {
    console.error('Error setting completion:', err);
  }
},


  deleteTask:async(id)=>{
    console.log('deleteTask')
    const result = await axios.delete(`${apiUrl}/delete/${id}`)    
    return result.data;
  }

};
